/*jshint unused:false */
/*global tinymce:true */

/**
 * Example plugin that adds a toolbar button and menu item.
 */
tinymce.PluginManager.add('insertc2a', function(editor, url) {
	// Add a button that opens a window
	editor.addButton('insertc2a', {
		text: '',
		icon: 'text',
		tooltip: 'Insert inline Call-to-action',
		onclick: function() {
			// Open window with a specific url
			editor.windowManager.open({
				title: 'Insert inline Call-to-action',
				url: '/flip/insertc2a/index.php',
				width: 600,
				height: 100,
				buttons: [
					{
						text: 'Insert',
						onclick: function() {
							// Top most window object
							var win = editor.windowManager.getWindows()[0];
							// Insert the contents of the dialog.html textarea into the editor
							var e = win.getContentWindow().document.getElementById('faq_cat');
							var insertVal = e.options[e.selectedIndex].value;
							if (insertVal) {
								editor.insertContent('<p>{'+e.options[e.selectedIndex].value+'}</p>');
								win.close();
							}
						}
					},
					{text: 'Close', onclick: 'close'}
				]
			});
		}
	});
});